max;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    int i,j,m;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    while(T--)//输入4X4矩阵&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        for(i=0;i&amp;lt;4;i++)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            for(j=0;j&amp;lt;4;j++)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                scanf(&amp;quot;%d&amp;quot;,&amp;amp;b[i][j]);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        m = 4;//旋转次数，转动90°&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        max = b[0][1]+b[0][0]+b[1][0]+b[1][1];//初始化，=0也可&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        while(m--)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            for(i=0;i&amp;lt;3;i++)//遍历矩阵寻找最大2X2子矩阵&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                for(j=0;j&amp;lt;3;j++)//注意不要大于4，会越界&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    if(b[i][j]+b[i][j+1]+b[i+1][j]+b[i+1][j+1] &amp;gt; max)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                        max = b[i][j]+b[i][j+1]+b[i+1][j]+b[i+1][j+1];&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt