gt;&lt;span class=&#34;line&#34;&gt;68&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;69&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;70&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;71&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;72&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;73&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;#include &amp;lt;stdio.h&amp;gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;#define N 1000000&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;int b[N*3]=&amp;#123;0,0&amp;#125;;//素数标记&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;char s[N]=&amp;#123;0&amp;#125;;//素数字符串&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;int c[10][N+2]=&amp;#123;0&amp;#125;;//一维表示数码，二维表示个数&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;int leng(int x)//计算位数 &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	int len = 0;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	while(x)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		len++;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		x /= 10;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	return len;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125; &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;int main()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    int i,j,len = 0,t,flag=0;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    for(i=2; i&amp;lt;=N*3; i++)//素数打表 &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        b[i]=1;//0,1已被初始化为0 &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    for(i=1; i&amp;lt;=2000; i++) //i不能太大 &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        if(b[i]) &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            for(j=i*i; j&amp;lt;=N*3; j=j+i)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                b[j]=0;//非素数&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    for(i=2;i&amp;lt;=N*3;i++)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    	if(b[i])//如果是素数 &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    	&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    		len = leng(i);//计算位数 &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    		for(int k=0;k&amp;lt;len;k++)//根据位数长度存储 ，拆开 &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    		&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    			t = i;//存素数 &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    			while(t)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    			&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    				b[flag+len-1] = t%10+&amp;#x27;0&amp;#x27;;//SPRINTF &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    				t /= 10;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    				flag++;//从0开始存储。flag不要重置 &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    				len -= 2;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;				&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;			&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	for(i=0;i&amp;lt;=N;i++)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		int num = b[i]-&amp;#x27;0&amp;#x27;;//题目要求 &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		for(int k=0;k&amp;lt;10;k++)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;			if(k==num)//类前缀和 &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;			    c[k][i] = c[k][i-1]+1;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;			else&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;			    c[k][i] = c[k][i-1];&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    int K;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    int L,R,d;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    scanf(&amp;quot;%d&amp;quot;,&amp;amp;K);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        while(K--)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            scanf(&amp;quot;%d %d %d&amp;quot;,&amp;amp;L,&amp;amp;R,&amp;amp;d);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            printf(&amp;quot;%d\n&amp;quot;,c[d][R-1]-c[d][L-1-1]);//规律 &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;&lt;/p&gt;

 &lt;h1 id=&#34;prime-twins&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#prime-twins&#34;&gt;#&lt;/a&gt; Prime Twins&lt;/h1&gt;
&lt;h2 id=&#34;题目描述&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#题目描述&#34;&gt;#&lt;/a&gt; 题目描述&lt;/h2&gt;
&lt;p&gt;如果 n 和 n+2 都是素数，我们称其为孪生素数，比如 3 和 5，5 和 7 都是孪生素数。 给你一个区间 [a,b], 请问期间有多少对孪生素数？&lt;/p&gt;
&lt;h2 id=&#34;输入&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#输入&#34;&gt;#&lt;/a&gt; 输入&lt;/h2&gt;
&lt;p&gt;第一行是一个整数 K (K≤ 10000)，表示样例的个数。 以后每行一个样例，为两个整数，a 和 b，1≤a≤b≤5000000。&lt;/p&gt;
&lt;h2 id=&#34;输出&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#输出&#34;&gt;#&lt;/a&gt; 输出&lt;/h2&gt;
&lt;p&gt;每行输出一个样例的结果。&lt;/p&gt;
&lt;h2 id=&#34;样例输入&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#样例输入&#34;&gt;#&lt;/a&gt; 样例输入&lt;/h2&gt;
