&gt;&lt;span class=&#34;line&#34;&gt;        int len = strlen(eng);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        for(i=0; i&amp;lt;len; i++)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            if(eng[i] == &amp;#x27; &amp;#x27;)&amp;#123;//can can 空格（单词数量）&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                cnt++;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                eng[i] = 0;//空格的不要计入字母的计数里&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &amp;#125;else if(eng[i] &amp;gt;= &amp;#x27;A&amp;#x27; &amp;amp;&amp;amp; eng[i] &amp;lt;= &amp;#x27;Z&amp;#x27;)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                eng[i] = eng[i]+32;//大写字母转小写&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &amp;#125;    &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        if(cnt &amp;gt;= 10)&amp;#123;//空格数量代表单词数量&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            printf(&amp;quot;No\n&amp;quot;);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;else&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            for(i=0; i&amp;lt;len; i++)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                if(eng[i])&amp;#123;//不等于0，也就是不是空格&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    count[eng[i]-&amp;#x27;a&amp;#x27;]++;//小写字母&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                &amp;#125;else&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    continue;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt