&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	int count = 1;//每次需要填充的数字&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	int i,j;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	while(loop--)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		i = startx;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		j = starty;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		//从上层从左到右&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		for(j = starty; j &amp;lt; n+starty-offset; j++)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;			a[i][j] = count++;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		//从右侧从上到下&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		for(i = startx; i &amp;lt; n+startx-offset; i++)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;			a[i][j] = count++;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		//从下层从右到左&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		for(; j &amp;gt; starty; j--)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;			a[i][j] = count++;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		//从左侧从下到上&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		for(; i &amp;gt; startx; i--)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;			a[i][j] = count++;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		//如果是奇数，需要把中间补上&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		if(n%2 == 1)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;			a[mid][mid] = count++;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		offset += 2;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		startx++;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		starty++;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	//打印出来看一下&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	for(i=0; i&amp;lt;n; i++)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		for(j=0; j&amp;lt;n; j++)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;			printf(&amp;quot;%d &amp;quot;,a[i][j]);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		printf(&amp;quot;\n&amp;quot;);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	return 0;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;

 &lt;h1 id=&#34;搜索插入位置&#34;&gt;&lt;a href=&#34;#搜索插入位置&#34; class=&#34;headerlink&#34; title=&#34;搜索插入位置&#34;&gt;&lt;/a&gt;搜索插入位置&lt;/h1&gt;&lt;h2 id=&#34;例题&#34;&gt;&lt;a href=&#34;#例题&#34; class=&#34;headerlink&#34; title=&#34;例题&#34;&gt;&lt;/a&gt;例题&lt;/h2&gt;&lt;p&gt;LEETCODE 35.搜索插入位置&lt;/p&gt;
&lt;h2 id=&#34;前言&#34;&gt;&lt;a href=&#34;#前言&#34; class=&#34;headerlink&#34; title=&#34;前言&#34;&gt;&lt;/a&gt;前言&lt;/h2&gt;&lt;p&gt;不想多说，暴力不一定时间复杂度低，二分不一定更高效。&lt;/p&gt;
&lt;h2 id=&#34;暴力法&#34;&gt;&lt;a href=&#34;#暴力法&#34; class=&#34;headerlink&#34; title=&#34;暴力法&#34;&gt;&lt;/a&gt;暴力法&lt;/h2&gt;&lt;figure class=&#34;highlight plaintext&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;int searchInsert(int* nums, int numsSize, int target)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    for(int i=0; i&amp;lt;numsSize; i++)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		//谁说暴力不好用的？&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        if(nums[i] &amp;gt;= target)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            return i;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    return numsSize;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
&lt;h2 id=&#34;二分法（闭区间）&#34;&gt;&lt;a href=&#34;#二分法（闭区间）&#34; class=&#34;headerlink&#34; title=&#34;二分法（闭区间）&#34;&gt;&lt;/a&gt;二分法（闭区间）&lt;/h2&gt;&lt;figure class=&#34;highlight plaintext&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;10&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;11&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;12&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;13&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;14&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;15&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;16&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;17&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;18&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;int searchInsert(int* nums, int numsSize, int target)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    int left = 0;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    int right = numsSize - 1;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    int middle;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    while(left &amp;lt;= right)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        middle = left + (right-left)/2;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		//与之前一样的&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        if(nums[middle] &amp;lt; target)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            left = middle + 1;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125; else if (nums[middle] &amp;gt; target)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            right = middle - 1;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125; else if (nums[middle] == target)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            return middle;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    return right+1;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	//如果没有就在查找区间末加上一个&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;

 &lt;h1 id=&#34;有序数组的平方&#34;&gt;&lt;a href=&#34;#有序数组的平方&#34; class=&#34;headerlink&#34; title=&#34;有序数组的平方&#34;&gt;&lt;/a&gt;有序数组的平方&lt;/h1&gt;&lt;h2 id=&#34;例题&#34;&gt;&lt;a href=&#34;#例题&#34; class=&#34;headerlink&#34; title=&#34;例题&#34;&gt;&lt;/a&gt;例题&lt;/h2&gt;&lt;p&gt;LEETCODE 977.有序数组的平方&lt;/p&gt;
&lt;h2 id=&#34;双指针法&#34;&gt;&lt;a href=&#34;#双指针法&#34; class=&#34;headerlink&#34; title=&#34;双指针法&#34;&gt;&lt;/a&gt;双指针法&lt;/h2&gt;&lt;p&gt;因为不太明白它是如何提交数组的，所以在这里copy了别人的代码，仅供参考。&lt;/p&gt;
&lt;figure class=&#34;highlight plaintext&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;10&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;11&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;12&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;13&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;14&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;15&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;16&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;17&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;18&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;19&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;20&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;21&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;22&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;23&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;24&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;25&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;26&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;27&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;28&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;29&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;int* sortedSquares(int* nums, int numsSize, int* returnSize)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    //返回的数组大小就是原数组大小&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    *returnSize = numsSize;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    //创建两个指针，right指向数组最后一位元素，left指向数组第一位元素&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    int right = numsSize - 1;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    int left = 0;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    //最后要返回的结果数组&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    int* ans = (int*)malloc(sizeof(int) * numsSize);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    int index;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    for(index = numsSize - 1; index &amp;gt;= 0; index--) &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        //左指针指向元素的平方&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        int lSquare = nums[left] * nums[left];&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        //右指针指向元素的平方&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        int rSquare = nums[right] * nums[right];&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        //若左指针指向元素平方比右指针指向元素平方大，将左指针指向元素平方放入结果数组。左指针右移一位&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        if(lSquare &amp;gt; rSquare) &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            ans[index] = lSquare;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            left++;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125; &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        //若右指针指向元素平方比左指针指向元素平方大，将右指针指向元素平方放入结果数组。右指针左移一位&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        else &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            ans[index] = rSquare;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            right--;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    //返回结果数组&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    return ans;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;

 &lt;h1 id=&#34;长度最小的数组&#34;&gt;&lt;a href=&#34;#长度最小的数组&#34; class=&#34;headerlink&#34; title=&#34;长度最小的数组&#34;&gt;&lt;/a&gt;长度最小的数组&lt;/h1&gt;&lt;h2 id=&#34;例题&#34;&gt;&lt;a href=&#34;#例题&#34; class=&#34;headerlink&#34; title=&#34;例题&#34;&gt;&lt;/a&gt;例题&lt;/h2&gt;&lt;p&gt;LEETCODE 209.长度最小的数组&lt;/p&gt;
&lt;h2 id=&#34;暴力算法&#34;&gt;&lt;a href=&#34;#暴力算法&#34; class=&#34;headerlink&#34; title=&#34;暴力算法&#34;&gt;&lt;/a&gt;暴力算法&lt;/h2&gt;&lt;p&gt;这道题目暴力解法当然是 两个for循环，然后不断的寻找符合条件的子序列.&lt;/p&gt;
&lt;p&gt;代码如下：&lt;/p&gt;
&lt;figure class=&#34;highlight plaintext&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;10&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;11&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;12&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;13&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;14&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;15&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;16&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;17&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;int minSubArrayLen(int target, int* nums, int numsSize)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    int result = INT32_MAX;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    int sum = 0;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    int len = 0;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    for(int i=0; i&amp;lt;numsSize; i++)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        sum = 0;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        for(int j=i; j&amp;lt;numsSize; j++)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            sum += nums[j];&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            if(sum &amp;gt;= target)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                len = j-i+1;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                result = result &amp;lt; len ? result : len;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                break;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    return result &amp;lt; INT32_MAX ? result : 0;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
&lt;h2 id=&#34;滑动窗口&#34;&gt;&lt;a href=&#34;#滑动窗口&#34; class=&#34;headerlink&#34; title=&#34;滑动窗口&#34;&gt;&lt;/a&gt;滑动窗口&lt;/h2&gt;&lt;p&gt;接下来就开始介绍数组操作中另一个重要的方法：&lt;strong&gt;滑动窗口&lt;/strong&gt;。&lt;/p&gt;
&lt;p&gt;所谓滑动窗口，就是&lt;strong&gt;不断的调节子序列的起始位置和终止位置&lt;/strong&gt;，从而得出我们要想的结果。&lt;/p&gt;
&lt;p&gt;在暴力解法中，是一个for循环滑动窗口的起始位置，一个for循环为滑动窗口的终止位置，用两个for循环 完成了一个不断搜索区间的过程。&lt;/p&gt;
&lt;p&gt;那么滑动窗口如何用一个for循环来完成这个操作呢。&lt;/p&gt;
&lt;p&gt;首先要思考 如果用一个for循环，那么应该表示 滑动窗口的起始位置，还是终止位置。&lt;/p&gt;
&lt;p&gt;如果只用一个for循环来表示 滑动窗口的起始位置，那么如何遍历剩下的终止位置？&lt;/p&gt;
&lt;p&gt;此时难免再次陷入 暴力解法的怪圈。&lt;/p&gt;
&lt;p&gt;所以 只用一个for循环，那么这个循环的索引，一定是表示 滑动窗口的终止位置。&lt;/p&gt;
&lt;p&gt;在本题中实现滑动窗口，主要确定如下三点：&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;窗口内是什么？&lt;/li&gt;
&lt;li&gt;如何移动窗口的起始位置？&lt;/li&gt;
&lt;li&gt;如何移动窗口的结束位置？&lt;/li&gt;
&lt;/ul&gt;
&lt;p&gt;窗口就是 满足其和 ≥ s 的长度最小的 连续 子数组。&lt;/p&gt;
&lt;p&gt;窗口的起始位置如何移动：如果当前窗口的值大于s了，窗口就要向前移动了（也就是该缩小了）。&lt;/p&gt;
&lt;p&gt;窗口的结束位置如何移动：窗口的结束位置就是遍历数组的指针，也就是for循环里的索引。&lt;/p&gt;
&lt;p&gt;解题的关键在于 窗口的起始位置如何移动。&lt;/p&gt;
&lt;p&gt;&lt;strong&gt;滑动窗口的精妙之处在于根据当前子序列和大小的情况，不断调节子序列的起始位置。从而将O(n^2)暴力解法降为O(n)。&lt;/strong&gt;&lt;/p&gt;
&lt;p&gt;代码如下：&lt;/p&gt;
&lt;figure class=&#34;highlight plaintext&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;10&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;11&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;12&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;13&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;14&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;15&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;int minSubArrayLen(int target, int* nums, int numsSize)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    int result = INT32_MAX;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    int i = 0;//滑动窗口起始位置&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    int sum = 0;//滑动窗口内元素总和&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    int len = 0;//滑动窗口的长度&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    for(int j=0; j&amp;lt;numsSize; j++)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        sum += nums[j];//累加并与target比较&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        while(sum &amp;gt;= target)&amp;#123;//不能用IF&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            len = j-i+1;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            result = result &amp;lt; len ? result : len;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            sum -= nums[i++];//滑动窗口向后移动减去初始位置的值&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    return result &amp;lt; INT32_MAX ? result : 0;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;

 &lt;h1 id=&#34;移除元素&#34;&gt;&lt;a href=&#34;#移除元素&#34; class=&#34;headerlink&#34; title=&#34;移除元素&#34;&gt;&lt;/a&gt;移除元素&lt;/h1&gt;&lt;h2 id=&#34;例题&#34;&gt;&lt;a href=&#34;#例题&#34; class=&#34;headerlink&#34; title=&#34;例题&#34;&gt;&lt;/a&gt;例题&lt;/h2&gt;&lt;p&gt;LEETCODE 27.移除元素&lt;/p&gt;
&lt;h2 id=&#34;暴力算法&#34;&gt;&lt;a href=&#34;#暴力算法&#34; class=&#34;headerlink&#34; title=&#34;暴力算法&#34;&gt;&lt;/a&gt;暴力算法&lt;/h2&gt;&lt;p&gt;这个题目暴力的解法就是两层for循环，一个for循环遍历数组元素 ，第二个for循环更新数组。&lt;/p&gt;
&lt;figure class=&#34;highlight plaintext&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;10&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;11&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;12&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;13&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;14&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;15&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;int removeElement(int* nums, int numsSize, int val)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    int i,j;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    int n = numsSize;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    for(i=0; i&amp;lt;numsSize; i++)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        if(nums[i] == val)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            for(j=i+1; j&amp;lt;numsSize; j++)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                nums[j-1] == nums[j];&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        i--;//每次去除VAL后，数组下标都会前移&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        n--;//长度也在减小&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    printf(&amp;quot;%d&amp;quot;,n);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    return 0;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
&lt;h2 id=&#34;双指针法&#34;&gt;&lt;a href=&#34;#双指针法&#34; class=&#34;headerlink&#34; title=&#34;双指针法&#34;&gt;&lt;/a&gt;双指针法&lt;/h2&gt;&lt;p&gt;双指针法（快慢指针法）： &lt;strong&gt;通过一个快指针和慢指针在一个for循环下完成两个for循环的工作。&lt;/strong&gt;&lt;/p&gt;
&lt;p&gt;定义快慢指针&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;快指针：寻找新数组的元素 ，新数组就是不含有目标元素的数组&lt;/li&gt;
&lt;li&gt;慢指针：指向更新 新数组下标的位置&lt;/li&gt;
&lt;/ul&gt;
&lt;p&gt;优点：&lt;br&gt;减少复杂度，优化代码。&lt;/p&gt;
&lt;p&gt;代码如下：&lt;/p&gt;
&lt;figure class=&#34;highlight plaintext&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;10&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;int removeElement(int* nums, int numsSize, int val)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    int fast = 0;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    int slow = 0;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    for(; fast&amp;lt;numsSize; fast++)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        if(nums[fast] != val)&amp;#123;//如果元素不是VAL，下标为slow的元素等于快指针处的元素&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            nums[slow++] = nums[fast];&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;//如果相等则跳过，此时slow未递增；快指针依旧在往前进&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    return slow;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;

 &lt;h1 id=&#34;二分查找&#34;&gt;&lt;a href=&#34;#二分查找&#34; class=&#34;headerlink&#34; title=&#34;二分查找&#34;&gt;&lt;/a&gt;二分查找&lt;/h1&gt;&lt;h2 id=&#34;例题&#34;&gt;&lt;a href=&#34;#例题&#34; class=&#34;headerlink&#34; title=&#34;例题&#34;&gt;&lt;/a&gt;例题&lt;/h2&gt;&lt;p&gt;LEETCODE 704.二分查找&lt;/p&gt;
&lt;h2 id=&#34;二分第一种写法&#34;&gt;&lt;a href=&#34;#二分第一种写法&#34; class=&#34;headerlink&#34; title=&#34;二分第一种写法&#34;&gt;&lt;/a&gt;二分第一种写法&lt;/h2&gt;&lt;p&gt;第一种写法，我们定义 target 是在一个在左闭右闭的区间里，&lt;strong&gt;也就是[left, right]&lt;/strong&gt; （&lt;strong&gt;这个很重要非常重要&lt;/strong&gt;）。&lt;br&gt;区间的定义这就决定了二分法的代码应该如何写，&lt;strong&gt;因为定义*target&lt;/strong&gt;在**[left, right]&lt;strong&gt;区间&lt;/strong&gt;，所以有如下两点：&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;while (left &amp;lt;&amp;#x3D; right) 要使用 &amp;lt;&amp;#x3D; ，因为left &amp;#x3D;&amp;#x3D; right是有意义的，所以使用 &amp;lt;&amp;#x3D;&lt;/li&gt;
&lt;li&gt;if (nums[middle] &amp;gt; target) right 要赋值为 middle - 1，因为当前这个nums[middle]一定不是target，那么接下来要查找的左区间结束下标位置就是 middle - 1&lt;/li&gt;
&lt;/ul&gt;
&lt;p&gt;&lt;strong&gt;代码如下：&lt;/strong&gt;&lt;/p&gt;
&lt;figure class=&#34;highlight plaintext&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;10&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;11&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;12&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;13&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;14&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;15&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;16&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;17&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;18&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;int search(int* nums, int numsSize, int target)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    int left = 0;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    int right = numsSize-1;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    //当left==right，区间[left, right]依然有效，所以用 &amp;lt;=&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    while(left &amp;lt;= right)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    	//防止两个int相加造成溢出，等同于相加除2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        int middle = left+(right-left)/2;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        if(nums[middle] &amp;gt; target)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            right = middle-1;//middle一定不是要找的数，所以找他前面一位&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;else if(nums[middle] &amp;lt; target)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            left = middle+1;//同上&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;else&amp;#123;//nums[middle] == target&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            return middle;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;   //未找到下标，返回-1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        return -1;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
&lt;h2 id=&#34;二分第二种写法&#34;&gt;&lt;a href=&#34;#二分第二种写法&#34; class=&#34;headerlink&#34; title=&#34;二分第二种写法&#34;&gt;&lt;/a&gt;二分第二种写法&lt;/h2&gt;&lt;p&gt;如果说定义 target 是在一个在左闭右开的区间里，也就是[left, right) ，那么二分法的边界处理方式则截然不同。&lt;/p&gt;
&lt;p&gt;有如下两点：&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;while (left &amp;lt; right)，这里使用 &amp;lt; ,因为left &amp;#x3D;&amp;#x3D; right在区间[left, right)是没有意义的&lt;/li&gt;
&lt;li&gt;if (nums[middle] &amp;gt; target) right 更新为 middle，因为当前nums[middle]不等于target，去左区间继续寻找，而寻找区间是左闭右开区间，所以right更新为middle，即：下一个查询区间不会去比较nums[middle]&lt;/li&gt;
&lt;/ul&gt;
&lt;p&gt;在数组：1,2,3,4,7,9,10中查找元素2，如图所示：&lt;strong&gt;（注意和方法一的区别）&lt;/strong&gt;&lt;/p&gt;
&lt;p&gt;&lt;strong&gt;代码如下：&lt;/strong&gt;&lt;/p&gt;
&lt;figure class=&#34;highlight plaintext&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;10&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;11&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;12&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;13&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;14&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;15&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;16&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;17&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;int search(int* nums, int numsSize, int target)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    int left = 0;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    int right = numsSize;//看不同&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    while(left &amp;lt; right)&amp;#123;//定义target在左闭右开的区间里，即：[left, right)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    	//==时无意义，会越界&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        int middle = left+(right-left)/2;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        if(nums[middle] &amp;gt; target)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            right = middle;//开区间，不会取到middle&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;else if(nums[middle] &amp;lt; target)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            left = middle+1;//前闭，所以会取到middle+1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;else&amp;#123;// nums[middle] == target&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            return middle;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;	//未找到下标，返回-1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        return -1;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;

 &lt;h1 id=&#34;空间换时间样例&#34;&gt;&lt;a href=&#34;#空间换时间样例&#34; class=&#34;headerlink&#34; title=&#34;空间换时间样例&#34;&gt;&lt;/a&gt;空间换时间样例&lt;/h1&gt;&lt;h2 id=&#34;前言&#34;&gt;&lt;a href=&#34;#前言&#34; class=&#34;headerlink&#34; title=&#34;前言&#34;&gt;&lt;/a&gt;前言&lt;/h2&gt;&lt;p&gt;比较懒，不想写太多，就整一个样例，统计字母串里出现次数最多的字母&lt;/p&gt;
&lt;h2 id=&#34;优化前&#34;&gt;&lt;a href=&#34;#优化前&#34; class=&#34;headerlink&#34; title=&#34;优化前&#34;&gt;&lt;/a&gt;优化前&lt;/h2&gt;&lt;p&gt;两层循环遍历字母串数组，统计出现最多次数字母的次数；&lt;br&gt;暴力算法就是两层循环枚举出现次数最多的字母；&lt;br&gt;显然时间复杂度太高，运算时间过长非常容易超时；&lt;br&gt;所以这里我们用空间换时间使用一个统计次数的数组，只需要遍历一次就可以了，时间复杂度只用O(n)&lt;/p&gt;
&lt;p&gt;&lt;strong&gt;代码如下&lt;/strong&gt;&lt;/p&gt;
&lt;h2 id=&#34;参考源码&#34;&gt;&lt;a href=&#34;#参考源码&#34; class=&#34;headerlink&#34; title=&#34;参考源码&#34;&gt;&lt;/a&gt;参考源码&lt;/h2&gt;&lt;figure class=&#34;highlight plaintext&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;10&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;11&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;12&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;13&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;14&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;15&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;16&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;17&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;18&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;19&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;20&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;21&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;22&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;23&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;/**找一串字母出现次数最多的字母**/&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;#include &amp;lt;stdio.h&amp;gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;#include &amp;lt;string.h&amp;gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;int main()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	int max=0;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	char ans;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	int record[26] = &amp;#123;0&amp;#125;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	char arr[1000];&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	gets(arr);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	int n = strlen(arr);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	for(int i=0; i&amp;lt;n; i++)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		record[arr[i]-&amp;#x27;a&amp;#x27;]++;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	for(int i=0; i&amp;lt;26; i++)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		if(record[i] &amp;gt; max)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;			max = record[i];&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;			ans = i+&amp;#x27;a&amp;#x27;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	printf(&amp;quot;%c %d\n&amp;quot;,ans,max);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	return 0;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;

 &lt;h1 id=&#34;字符串逆序&#34;&gt;&lt;a href=&#34;#字符串逆序&#34; class=&#34;headerlink&#34; title=&#34;字符串逆序&#34;&gt;&lt;/a&gt;字符串逆序&lt;/h1&gt;&lt;h2 id=&#34;Description&#34;&gt;&lt;a href=&#34;#Description&#34; class=&#34;headerlink&#34; title=&#34;Description&#34;&gt;&lt;/a&gt;Description&lt;/h2&gt;&lt;p&gt;给你一个字符串，请将其第K个字符开始的长度为L的子串进行逆序。&lt;/p&gt;
&lt;h2 id=&#34;输入&#34;&gt;&lt;a href=&#34;#输入&#34; class=&#34;headerlink&#34; title=&#34;输入&#34;&gt;&lt;/a&gt;输入&lt;/h2&gt;&lt;p&gt;有多组样例，第一行是一个整数，表示有多少个样例。每个样例占两行，第一行是个字符串，字符串全部由英文小写字符组成，长度不超过200。第二行是两个整数K和L，K从1开始计数，K+L-1不会超过字符串长度。&lt;/p&gt;
&lt;h2 id=&#34;输出&#34;&gt;&lt;a href=&#34;#输出&#34; class=&#34;headerlink&#34; title=&#34;输出&#34;&gt;&lt;/a&gt;输出&lt;/h2&gt;&lt;p&gt;每行输出一个字符串逆序以后的字符串结果。&lt;/p&gt;
&lt;h2 id=&#34;Sample-Input&#34;&gt;&lt;a href=&#34;#Sample-Input&#34; class=&#34;headerlink&#34; title=&#34;Sample Input&#34;&gt;&lt;/a&gt;Sample Input&lt;/h2&gt;&lt;figure class=&#34;highlight plaintext&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;abcabc&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;1 2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;abcabc&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2 5&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
&lt;h2 id=&#34;Sample-Output&#34;&gt;&lt;a href=&#34;#Sample-Output&#34; class=&#34;headerlink&#34; title=&#34;Sample Output&#34;&gt;&lt;/a&gt;Sample Output&lt;/h2&gt;&lt;figure class=&#34;highlight plaintext&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;bacabc&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;acbacb&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
&lt;h2 id=&#34;参考源码&#34;&gt;&lt;a href=&#34;#参考源码&#34; class=&#34;headerlink&#34; title=&#34;参考源码&#34;&gt;&lt;/a&gt;参考源码&lt;/h2&gt;&lt;figure class=&#34;highlight plaintext&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;10&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;11&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;12&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;13&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;14&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;15&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;16&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;17&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;18&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;19&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;20&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;21&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;22&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;23&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;24&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;25&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;26&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;27&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;28&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;29&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;30&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;31&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;#include &amp;lt;stdio.h&amp;gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;#include &amp;lt;string.h&amp;gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;int main()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    int n;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    char str[203];&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    scanf(&amp;quot;%d&amp;quot;,&amp;amp;n);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    while(n--)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        scanf(&amp;quot; %s&amp;quot;,&amp;amp;str);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        int k,l;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        scanf(&amp;quot;%d %d&amp;quot;,&amp;amp;k,&amp;amp;l);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        int len = strlen(str);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        int i;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        for(i=0;i&amp;lt;k-1;i++)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            printf(&amp;quot;%c&amp;quot;,str[i]);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        for(i=k+l-2;i&amp;gt;=k-1;i--)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            printf(&amp;quot;%c&amp;quot;,str[i]);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        for(i=k+l-1;i&amp;lt;len;i++)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            printf(&amp;quot;%c&amp;quot;,str[i]);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        printf(&amp;quot;\n&amp;quot;);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    return 0;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;

 &lt;h1 id=&#34;共同的前缀&#34;&gt;&lt;a href=&#34;#共同的前缀&#34; class=&#34;headerlink&#34; title=&#34;共同的前缀&#34;&gt;&lt;/a&gt;共同的前缀&lt;/h1&gt;&lt;h2 id=&#34;Description&#34;&gt;&lt;a href=&#34;#Description&#34; class=&#34;headerlink&#34; title=&#34;Description&#34;&gt;&lt;/a&gt;Description&lt;/h2&gt;&lt;p&gt;给你K个字符串，请求出它们的最长公共前缀。 输入 第一行是一个整数N，表示测试样例的个数。 每个测试样例的第一行是一个整数K（2 &amp;lt;&amp;#x3D; k &amp;lt;&amp;#x3D; 20），表示有多少个字符串；以后每行是一个字符串，每个字符串的长度不超过200个字符。 输出 每行输出一个样例的结果。先输出“Case #: ”，其中’#’为样例的序号（从1开始），冒号为英文冒号，后接一个空格；然后是对应样例的结果。如果没有公共前缀，则无需输出前缀，但Case信息仍需要输出。&lt;/p&gt;
